// SPDX-License-Identifier:  MIT
pragma solidity >=0.6.12;

import {
    ILendingPool,
    IProtocolDataProvider,
    IStableDebtToken,
    ILendingPoolAddressesProvider
} from "./Interfaces.sol";
import {IERC20} from "./token/ERC20/IERC20.sol";
import {SafeERC20} from "./token/ERC20/SafeERC20.sol";
import "./math/SafeMath.sol";
import "./utils/ArrayUtils.sol";
import "./utils/TimeHelpers.sol";
import "./utils/MathHelpers.sol";

/**
 * @title Conviction Voting for Credit Delegation contract
 */
contract ConvictionVotingCreditDelegation is TimeHelpers {
    using SafeMath for uint256;
    using ArrayUtils for uint256[];
    using SafeERC20 for IERC20;

    ILendingPoolAddressesProvider provider;
    ILendingPool lendingPool;
    IProtocolDataProvider constant dataProvider =
        IProtocolDataProvider(
            address(0x744C1aaA95232EeF8A9994C4E0b3a89659D9AB79)
        );

    bytes32 public constant UPDATE_SETTINGS_ROLE =
        keccak256("UPDATE_SETTINGS_ROLE");
    bytes32 public constant CREATE_PROPOSALS_ROLE =
        keccak256("CREATE_PROPOSALS_ROLE");
    bytes32 public constant CANCEL_PROPOSAL_ROLE =
        keccak256("CANCEL_PROPOSAL_ROLE");

    uint256 public constant D = 10000000;
    uint256 public constant ONE_HUNDRED_PERCENT = 1e18;
    uint256 private constant TWO_128 = 0x100000000000000000000000000000000; // 2^128
    uint256 private constant TWO_127 = 0x80000000000000000000000000000000; // 2^127
    uint256 private constant TWO_64 = 0x10000000000000000; // 2^64
    uint64 public constant MAX_STAKED_PROPOSALS = 10;

    string private constant ERROR_PROPOSAL_DOES_NOT_EXIST =
        "CVCD_PROPOSAL_DOES_NOT_EXIST";
    string private constant ERROR_STAKING_ALREADY_STAKED =
        "CVCD_STAKING_ALREADY_STAKED";
    string private constant ERROR_PROPOSAL_NOT_ACTIVE =
        "CVCD_PROPOSAL_NOT_ACTIVE";
    string private constant ERROR_CANNOT_EXECUTE_ABSTAIN_PROPOSAL =
        "CVCD_CANNOT_EXECUTE_ABSTAIN_PROPOSAL";
    string private constant ERROR_CANNOT_EXECUTE_ZERO_VALUE_PROPOSAL =
        "CVCD_CANNOT_EXECUTE_ZERO_VALUE_PROPOSAL";
    string private constant ERROR_INSUFFICIENT_CONVICION =
        "CVCD_INSUFFICIENT_CONVICION";
    string private constant ERROR_SENDER_CANNOT_CANCEL =
        "CVCD_SENDER_CANNOT_CANCEL";
    string private constant ERROR_CANNOT_CANCEL_ABSTAIN_PROPOSAL =
        "CVCD_CANNOT_CANCEL_ABSTAIN_PROPOSAL";
    string private constant ERROR_AMOUNT_OVER_MAX_RATIO =
        "CVCD_AMOUNT_OVER_MAX_RATIO";
    string private constant ERROR_INCORRECT_TOKEN_MANAGER_HOOK =
        "CVCD_INCORRECT_TOKEN_MANAGER_HOOK";
    string private constant ERROR_AMOUNT_CAN_NOT_BE_ZERO =
        "CVCD_AMOUNT_CAN_NOT_BE_ZERO";
    string private constant ERROR_STAKING_MORE_THAN_AVAILABLE =
        "CVCD_STAKING_MORE_THAN_AVAILABLE";
    string private constant ERROR_MAX_PROPOSALS_REACHED =
        "CVCD_MAX_PROPOSALS_REACHED";
    string private constant ERROR_WITHDRAW_MORE_THAN_STAKED =
        "CVCD_WITHDRAW_MORE_THAN_STAKED";
    string private constant ERROR_ONLY_OWNER =
        "CVCD_ONLY_OWNER_ALLOWED";
    string private constant ERROR_CAN_NOT_WHITHDRAW_WHILE_PROJECT_EXECUTED =
        "CVCD_CAN_NOT_WHITHDRAW_WHILE_PROJECT_EXECUTED";
    string private constant ERROR_ALLOWANCE_TOO_LOW =
        "CVCD_ALLOWANCE_FOR_CREDIT_TOO_LOW";

    enum ProposalStatus {Active, Cancelled, Executed, Repaid}

    struct Proposal {
        uint256 requestedAmount;
        address beneficiary;
        uint256 stakedTokens;
        uint256 convictionLast;
        uint64 blockLast;
        ProposalStatus proposalStatus;
        mapping(address => uint256) voterStake;
        address submitter;
    }

    address public owner;
    address public requestToken;
    uint256 public decay;
    uint256 public maxRatio;
    uint256 public weight;
    uint256 public minThresholdStakePercentage;
    uint256 public proposalCounter;
    uint256 public totalStaked;
    uint256 public totalDeposited;

    mapping(uint256 => Proposal) internal proposals;
    mapping(address => uint256) internal totalVoterStake;
    mapping(address => uint256) internal totalAccountDeposited;
    mapping(address => uint256) internal totalAccountBorrowed;
    mapping(address => uint256[]) internal voterStakedProposals;

    event ConvictionSettingsChanged(
        uint256 decay,
        uint256 maxRatio,
        uint256 weight,
        uint256 minThresholdStakePercentage
    );
    event ProposalAdded(
        address indexed entity,
        uint256 indexed id,
        string title,
        bytes link,
        uint256 amount,
        address beneficiary
    );
    event StakeAdded(
        address indexed entity,
        uint256 indexed id,
        uint256 amount,
        uint256 tokensStaked,
        uint256 totalTokensStaked,
        uint256 conviction
    );
    event StakeWithdrawn(
        address entity,
        uint256 indexed id,
        uint256 amount,
        uint256 tokensStaked,
        uint256 totalTokensStaked,
        uint256 conviction
    );
    event ProposalExecuted(uint256 indexed id, uint256 conviction);
    event ProposalCancelled(uint256 indexed id);

    modifier onlyOwner() {
        require(owner == msg.sender, ERROR_ONLY_OWNER);
        _;
    }

    modifier proposalExists(uint256 _proposalId) {
        require(
            _proposalId == 1 || proposals[_proposalId].submitter != address(0),
            ERROR_PROPOSAL_DOES_NOT_EXIST
        );
        _;
    }

    constructor(
        address _requestToken,
        uint256 _decay,
        uint256 _maxRatio,
        uint256 _weight,
        uint256 _minThresholdStakePercentage
    ) public {
        // Kovan Aave
        provider = ILendingPoolAddressesProvider(
            address(0x652B2937Efd0B5beA1c8d54293FC1289672AFC6b)
        );
        lendingPool = ILendingPool(provider.getLendingPool());
        owner = msg.sender;
        proposalCounter = 0;
        requestToken = _requestToken;
        decay = _decay;
        maxRatio = _maxRatio;
        weight = _weight;
        minThresholdStakePercentage = _minThresholdStakePercentage;
    }

    /**
     * @notice Update the conviction voting parameters
     * @param _decay The rate at which conviction is accrued or lost from a proposal
     * @param _maxRatio Proposal threshold parameter
     * @param _weight Proposal threshold parameter
     * @param _minThresholdStakePercentage The minimum percent of stake token max supply that is used for calculating
        conviction
     */
    function setConvictionCalculationSettings(
        uint256 _decay,
        uint256 _maxRatio,
        uint256 _weight,
        uint256 _minThresholdStakePercentage
    ) public onlyOwner() {
        decay = _decay;
        maxRatio = _maxRatio;
        weight = _weight;
        minThresholdStakePercentage = _minThresholdStakePercentage;

        emit ConvictionSettingsChanged(
            _decay,
            _maxRatio,
            _weight,
            _minThresholdStakePercentage
        );
    }

    /**
     * @notice Add proposal `_title` for  `@tokenAmount((self.requestToken(): address), _requestedAmount)` to `_beneficiary`
     * @param _title Title of the proposal
     * @param _link IPFS or HTTP link with proposal's description
     * @param _requestedAmount Tokens requested
     * @param _beneficiary Address that will receive payment
     */
    function addProposal(
        string memory _title,
        bytes memory _link,
        uint256 _requestedAmount,
        address _beneficiary
    ) external {
        Proposal storage newProposal = proposals[proposalCounter++];
        newProposal.requestedAmount = _requestedAmount;
        newProposal.beneficiary = _beneficiary;
        newProposal.stakedTokens = 0;
        newProposal.convictionLast = 0;
        newProposal.blockLast = 0;
        newProposal.proposalStatus = ProposalStatus.Active;
        newProposal.submitter = msg.sender;

        emit ProposalAdded(
            msg.sender,
            proposalCounter,
            _title,
            _link,
            _requestedAmount,
            _beneficiary
        );
    }

    /**
     * Deposits collateral into Aave to enable credit delegation
     * This would be called by the delegator.
     * @param _amount The amount to be deposited as collateral
     * @param _isPull Whether to pull the funds from the caller, or use funds sent to this contract
     *  User must have approved this contract to pull funds if `isPull` = true
     *
     */
    function depositCollateral(uint256 _amount, bool _isPull) public {
        if (_isPull) {
            IERC20(requestToken).safeTransferFrom(msg.sender, address(this), _amount);
        }
        IERC20(requestToken).safeApprove(address(lendingPool), _amount);
        lendingPool.deposit(requestToken, _amount, address(this), 0);

        totalAccountDeposited[msg.sender] = totalAccountDeposited[msg.sender]
            .add(_amount);
        totalDeposited = totalDeposited.add(_amount);
    }


    /**
     * Borrows part of the uncollaterised loan
     * @param _proposalId Id of the proposal
     * @param _amount The amount the borrower is allowed to borrow (i.e. their line of credit)
     */
    function borrowCredit(uint256 _proposalId, uint256 _amount) public {
        Proposal storage proposal = proposals[_proposalId];
        require(msg.sender == proposal.beneficiary);
        require(proposal.proposalStatus == ProposalStatus.Executed);
        require(
            checkAllowance(address(this), msg.sender, requestToken) >= _amount,
            ERROR_ALLOWANCE_TOO_LOW
        );
        lendingPool.borrow(requestToken, _amount, 1, 0, address(this));

        totalAccountBorrowed[msg.sender] = totalAccountBorrowed[msg.sender].add(
            _amount
        );
    }

    /**
     * Checks allowance for loan
     * @param _delegator The delegator of the funds
     * @param _delegatee The delegatee of the funds
     * @param _asset The asset they are allowed to borrow
     */
    function checkAllowance(
        address _delegator,
        address _delegatee,
        address _asset
    ) public view returns (uint256) {
        (, address stableDebtTokenAddress, ) =
            dataProvider.getReserveTokensAddresses(_asset);
        return
            IStableDebtToken(stableDebtTokenAddress).borrowAllowance(
                _delegator,
                _delegatee
            );
    }

    /**
     * Repay an uncollaterised loan
     * @param _proposalId Id of the proposal
     * @param _amount The amount to repay
     *
     * User calling this function must have approved this contract with an allowance to transfer the tokens
     *
     * You should keep internal accounting of borrowers, if your contract will have multiple borrowers
     */
    function repayLoan(uint256 _proposalId, uint256 _amount) public {
        // TODO: Check how much the sender has to repay
        Proposal storage proposal = proposals[_proposalId];
        require(msg.sender == proposal.beneficiary);
        // TODO: check if the amount is accurate
        require(_amount == proposal.requestedAmount);
        IERC20(requestToken).safeTransferFrom(msg.sender, address(this), _amount);
        IERC20(requestToken).safeApprove(address(lendingPool), _amount);
        lendingPool.repay(requestToken, _amount, 1, address(this));

        proposal.proposalStatus = ProposalStatus.Repaid;
        totalAccountBorrowed[msg.sender] = totalAccountBorrowed[msg.sender].sub(
            _amount
        );
    }

    /**
     * Withdraw all of a collateral of the underlying asset, if no outstanding loans delegated
     * @param _asset The underlying asset to withdraw
     *
     * Add permissions to this call, e.g. only the owner should be able to withdraw the collateral!
     */
    function withdrawCollateralFromProposal(uint256 _proposalId, address _asset) public {
        Proposal storage proposal = proposals[_proposalId];
        require(proposal.proposalStatus == ProposalStatus.Repaid);
        uint256 voterStaked = proposal.voterStake[msg.sender];
        require(voterStaked > 0);
        (address aTokenAddress, , ) =
            dataProvider.getReserveTokensAddresses(_asset);
        uint256 assetBalance = IERC20(aTokenAddress).balanceOf(address(this));
        lendingPool.withdraw(_asset, assetBalance, msg.sender);
        totalAccountDeposited[msg.sender] = totalAccountDeposited[msg.sender].sub(voterStaked);
		totalDeposited = totalDeposited.sub(voterStaked);
    }

    /**
     * Withdraw all of a collateral of the underlying asset, if no outstanding loans delegated
     * @param _asset The underlying asset to withdraw
     *
     * Add permissions to this call, e.g. only the owner should be able to withdraw the collateral!
     */
    function withdrawAllCollateral(address _asset) public {
        (address aTokenAddress, , ) =
            dataProvider.getReserveTokensAddresses(_asset);
        // TODO: change the assetBalance here and the receiver of the rewards
        uint256 assetBalance = IERC20(aTokenAddress).balanceOf(address(this));
        lendingPool.withdraw(_asset, assetBalance, owner);
        totalAccountDeposited[msg.sender] = totalAccountDeposited[msg.sender].sub(assetBalance);
		totalDeposited = totalDeposited.sub(assetBalance);
    }

    /**
     * @notice Stake `@tokenAmount((self.stakeToken(): address), _amount)` on proposal #`_proposalId`
     * @param _proposalId Proposal id
     * @param _amount Amount of tokens staked
     */
    function stakeToProposal(uint256 _proposalId, uint256 _amount) external {
        _stake(_proposalId, _amount, msg.sender);
    }

    /**
     * @notice Stake all my `(self.stakeToken(): address).symbol(): string` tokens on proposal #`_proposalId`
     * @param _proposalId Proposal id
     */
    function stakeAllToProposal(uint256 _proposalId) external {
        require(totalVoterStake[msg.sender] == 0, ERROR_STAKING_ALREADY_STAKED);
        _stake(_proposalId, totalAccountDeposited[msg.sender], msg.sender);
    }

    /**
     * @notice Withdraw `@tokenAmount((self.stakeToken(): address), _amount)` previously staked on proposal #`_proposalId`
     * @param _proposalId Proposal id
     * @param _amount Amount of tokens withdrawn
     */
    function withdrawStakedFromProposal(uint256 _proposalId, uint256 _amount)
        external
        proposalExists(_proposalId)
    {
        _withdrawStakedFromProposal(_proposalId, _amount, msg.sender);
    }

    /**
     * @notice Withdraw all `(self.stakeToken(): address).symbol(): string` tokens previously staked on proposal #`_proposalId`
     * @param _proposalId Proposal id
     */
    function withdrawAllStakedFromProposal(uint256 _proposalId)
        external
        proposalExists(_proposalId)
    {
        _withdrawStakedFromProposal(
            _proposalId,
            proposals[_proposalId].voterStake[msg.sender],
            msg.sender
        );
    }

    /**
     * @notice Execute proposal #`_proposalId`
     * @dev ...by sending `@tokenAmount((self.requestToken(): address), self.getPropoal(_proposalId): ([uint256], address, uint256, uint256, uint64, bool))` to `self.getPropoal(_proposalId): (uint256, [address], uint256, uint256, uint64, bool)`
     * @param _proposalId Proposal id
     */
    function executeProposal(uint256 _proposalId)
        external
        proposalExists(_proposalId)
    {
        Proposal storage proposal = proposals[_proposalId];

        require(
            proposal.requestedAmount > 0,
            ERROR_CANNOT_EXECUTE_ZERO_VALUE_PROPOSAL
        );
        require(
            proposal.proposalStatus == ProposalStatus.Active,
            ERROR_PROPOSAL_NOT_ACTIVE
        );
        _calculateAndSetConviction(proposal, proposal.stakedTokens);
        require(
            proposal.convictionLast >
                calculateThreshold(proposal.requestedAmount),
            ERROR_INSUFFICIENT_CONVICION
        );

        proposal.proposalStatus = ProposalStatus.Executed;
        _approveBorrower(_proposalId, proposal.requestedAmount);

        emit ProposalExecuted(_proposalId, proposal.convictionLast);
    }

    /**
     * @notice Cancel proposal #`_proposalId`
     * @param _proposalId Proposal id
     */
    function cancelProposal(uint256 _proposalId)
        external
        proposalExists(_proposalId)
    {
        Proposal storage proposal = proposals[_proposalId];

        require(proposal.submitter == msg.sender);
        require(
            proposal.proposalStatus == ProposalStatus.Active,
            ERROR_PROPOSAL_NOT_ACTIVE
        );

        proposal.proposalStatus = ProposalStatus.Cancelled;

        emit ProposalCancelled(_proposalId);
    }

    /**
     * @dev Get proposal details
     * @param _proposalId Proposal id
     * @return requestedAmount Requested amount
     * @return beneficiary Beneficiary address
     * @return stakedTokens Current total stake of tokens on this proposal
     * @return convictionLast Conviction this proposal had last time calculateAndSetConviction was called
     * @return blockLast Block when calculateAndSetConviction was called
     * @return proposalStatus ProposalStatus defining the state of the proposal
     * @return submitter Submitter of the proposal
     */
    function getProposal(uint256 _proposalId)
        public
        view
        returns (
            uint256 requestedAmount,
            address beneficiary,
            uint256 stakedTokens,
            uint256 convictionLast,
            uint64 blockLast,
            ProposalStatus proposalStatus,
            address submitter
        )
    {
        Proposal storage proposal = proposals[_proposalId];
        return (
            proposal.requestedAmount,
            proposal.beneficiary,
            proposal.stakedTokens,
            proposal.convictionLast,
            proposal.blockLast,
            proposal.proposalStatus,
            proposal.submitter
        );
    }

    /**
     * @notice Get stake of voter `_voter` on proposal #`_proposalId`
     * @param _proposalId Proposal id
     * @param _voter Voter address
     * @return Proposal voter stake
     */
    function getProposalVoterStake(uint256 _proposalId, address _voter)
        public
        view
        returns (uint256)
    {
        return proposals[_proposalId].voterStake[_voter];
    }

    /**
     * @notice Get the total stake of voter `_voter` on all proposals
     * @param _voter Voter address
     * @return Total voter stake
     */
    function getTotalVoterStake(address _voter) public view returns (uint256) {
        return totalVoterStake[_voter];
    }

    /**
     * @notice Get all proposal ID's voter `_voter` has currently staked to
     * @param _voter Voter address
     * @return Voter proposals
     */
    function getVoterStakedProposals(address _voter)
        public
        view
        returns (uint256[] memory)
    {
        return voterStakedProposals[_voter];
    }

    /**
     * @dev Conviction formula: a^t * y(0) + x * (1 - a^t) / (1 - a)
     * Solidity implementation: y = (2^128 * a^t * y0 + x * D * (2^128 - 2^128 * a^t) / (D - aD) + 2^127) / 2^128
     * @param _timePassed Number of blocks since last conviction record
     * @param _lastConv Last conviction record
     * @param _oldAmount Amount of tokens staked until now
     * @return Current conviction
     */
    function calculateConviction(
        uint64 _timePassed,
        uint256 _lastConv,
        uint256 _oldAmount
    ) public view returns (uint256) {
        uint256 t = uint256(_timePassed);
        // atTWO_128 = 2^128 * a^t
        uint256 atTWO_128 = _pow((decay << 128).div(D), t);
        // solium-disable-previous-line
        // conviction = (atTWO_128 * _lastConv + _oldAmount * D * (2^128 - atTWO_128) / (D - aD) + 2^127) / 2^128
        return
            (
                atTWO_128.mul(_lastConv).add(
                    _oldAmount.mul(D).mul(TWO_128.sub(atTWO_128)).div(D - decay)
                )
            )
                .add(TWO_127) >> 128;
    }

    /**
     * @dev Formula: ρ * totalStaked / (1 - a) / (β - requestedAmount / total)**2
     * For the Solidity implementation we amplify ρ and β and simplify the formula:
     * weight = ρ * D
     * maxRatio = β * D
     * decay = a * D
     * threshold = weight * totalStaked * D ** 2 * funds ** 2 / (D - decay) / (maxRatio * funds - requestedAmount * D) ** 2
     * @param _requestedAmount Requested amount of tokens on certain proposal
     * @return _threshold Threshold a proposal's conviction should surpass in order to be able to
     * executed it.
     */
    function calculateThreshold(uint256 _requestedAmount)
        public
        view
        returns (uint256 _threshold)
    {
        require(
            maxRatio.mul(totalDeposited) > _requestedAmount.mul(D),
            ERROR_AMOUNT_OVER_MAX_RATIO
        );
        // denom = maxRatio * 2 ** 64 / D  - requestedAmount * 2 ** 64 / funds
        uint256 denom =
            (maxRatio << 64).div(D).sub(
                (_requestedAmount << 64).div(totalDeposited)
            );
        // _threshold = (weight * 2 ** 128 / D) / (denom ** 2 / 2 ** 64) * totalStaked * D / 2 ** 128
        _threshold =
            ((weight << 128).div(D).div(denom.mul(denom) >> 64))
                .mul(D)
                .div(D.sub(decay))
                .mul(_totalStaked()) >>
            64;
    }

    function _approveBorrower(uint256 _proposalId, uint256 _amount) internal {
        Proposal storage proposal = proposals[_proposalId];
        (, address stableDebtTokenAddress, ) = dataProvider.getReserveTokensAddresses(requestToken);
        IStableDebtToken(stableDebtTokenAddress).approveDelegation(
            proposal.beneficiary,
            _amount
        );
    }

    function _totalStaked() internal view returns (uint256) {
        uint256 minTotalStake =
            (totalDeposited.mul(minThresholdStakePercentage)).div(
                ONE_HUNDRED_PERCENT
            );
        return totalStaked < minTotalStake ? minTotalStake : totalStaked;
    }

    /**
     * Multiply _a by _b / 2^128.  Parameter _a should be less than or equal to
     * 2^128 and parameter _b should be less than 2^128.
     * @param _a left argument
     * @param _b right argument
     * @return _result _a * _b / 2^128
     */
    function _mul(uint256 _a, uint256 _b)
        internal
        pure
        returns (uint256 _result)
    {
        require(_a <= TWO_128, "_a should be less than or equal to 2^128");
        require(_b < TWO_128, "_b should be less than 2^128");
        return _a.mul(_b).add(TWO_127) >> 128;
    }

    /**
     * Calculate (_a / 2^128)^_b * 2^128.  Parameter _a should be less than 2^128.
     *
     * @param _a left argument
     * @param _b right argument
     * @return _result (_a / 2^128)^_b * 2^128
     */
    function _pow(uint256 _a, uint256 _b)
        internal
        pure
        returns (uint256 _result)
    {
        require(_a < TWO_128, "_a should be less than 2^128");
        uint256 a = _a;
        uint256 b = _b;
        _result = TWO_128;
        while (b > 0) {
            if (b & 1 == 0) {
                a = _mul(a, a);
                b >>= 1;
            } else {
                _result = _mul(_result, a);
                b -= 1;
            }
        }
    }

    /**
     * @dev Calculate conviction and store it on the proposal
     * @param _proposal Proposal
     * @param _oldStaked Amount of tokens staked on a proposal until now
     */
    function _calculateAndSetConviction(
        Proposal storage _proposal,
        uint256 _oldStaked
    ) internal {
        uint64 blockNumber = getBlockNumber64();
        assert(_proposal.blockLast <= blockNumber);
        if (_proposal.blockLast == blockNumber) {
            return; // Conviction already stored
        }
        // calculateConviction and store it
        uint256 conviction =
            calculateConviction(
                blockNumber - _proposal.blockLast, // we assert it doesn't overflow above
                _proposal.convictionLast,
                _oldStaked
            );
        _proposal.blockLast = blockNumber;
        _proposal.convictionLast = conviction;
    }

    /**
     * @dev Stake an amount of tokens on a proposal
     * @param _proposalId Proposal id
     * @param _amount Amount of staked tokens
     * @param _from Account from which we stake
     */
    function _stake(
        uint256 _proposalId,
        uint256 _amount,
        address _from
    ) internal proposalExists(_proposalId) {
        Proposal storage proposal = proposals[_proposalId];
        require(_amount > 0, ERROR_AMOUNT_CAN_NOT_BE_ZERO);
        require(
            proposal.proposalStatus == ProposalStatus.Active,
            ERROR_PROPOSAL_NOT_ACTIVE
        );

        uint256 unstakedAmount =
            totalAccountDeposited[_from].sub(totalVoterStake[_from]);
        if (_amount > unstakedAmount) {
            _withdrawInactiveStakedTokens(_amount.sub(unstakedAmount), _from);
        }

        require(
            totalVoterStake[_from].add(_amount) <= totalAccountDeposited[_from],
            ERROR_STAKING_MORE_THAN_AVAILABLE
        );

        uint256 previousStake = proposal.stakedTokens;
        proposal.stakedTokens = proposal.stakedTokens.add(_amount);
        proposal.voterStake[_from] = proposal.voterStake[_from].add(_amount);
        totalVoterStake[_from] = totalVoterStake[_from].add(_amount);
        totalStaked = totalStaked.add(_amount);

        if (proposal.blockLast == 0) {
            proposal.blockLast = getBlockNumber64();
        } else {
            _calculateAndSetConviction(proposal, previousStake);
        }

        _updateVoterStakedProposals(_proposalId, _from);

        emit StakeAdded(
            _from,
            _proposalId,
            _amount,
            proposal.voterStake[_from],
            proposal.stakedTokens,
            proposal.convictionLast
        );
    }

    function _updateVoterStakedProposals(
        uint256 _proposalId,
        address _submitter
    ) internal {
        uint256[] storage voterStakedProposalsArray =
            voterStakedProposals[_submitter];

        if (!voterStakedProposalsArray.contains(_proposalId)) {
            require(
                voterStakedProposalsArray.length < MAX_STAKED_PROPOSALS,
                ERROR_MAX_PROPOSALS_REACHED
            );
            voterStakedProposalsArray.push(_proposalId);
        }
    }

    /**
     * @dev Withdraw staked tokens from executed proposals until a target amount is reached.
     * @param _targetAmount Target at which to stop withdrawing tokens
     * @param _from Account to withdraw from
     */
    function _withdrawInactiveStakedTokens(uint256 _targetAmount, address _from)
        internal
    {
        uint256 i = 0;
        uint256 toWithdraw;
        uint256 withdrawnAmount = 0;
        uint256[] memory voterStakedProposalsCopy = voterStakedProposals[_from];

        while (
            i < voterStakedProposalsCopy.length &&
            withdrawnAmount < _targetAmount
        ) {
            uint256 proposalId = voterStakedProposalsCopy[i];
            Proposal storage proposal = proposals[proposalId];

            // Withdraw only form Cancelled proposals
            if ((proposal.proposalStatus == ProposalStatus.Cancelled)) {
                toWithdraw = proposal.voterStake[_from];
                if (toWithdraw > 0) {
                    _withdrawStakedFromProposal(proposalId, toWithdraw, _from);
                    withdrawnAmount = withdrawnAmount.add(toWithdraw);
                }
            }
            i++;
        }
    }

    /**
     * @dev Withdraw staked tokens from active proposals until a target amount is reached.
     * @param _targetAmount Target at which to stop withdrawing tokens
     * @param _from Account to withdraw from
     */
    function _withdrawActiveStakedTokens(uint256 _targetAmount, address _from)
        internal
    {
        uint256 i = 0;
        uint256 toWithdraw;
        uint256 withdrawnAmount = 0;
        uint256[] memory voterStakedProposalsCopy = voterStakedProposals[_from];

        // We reset this variable as _withdrawStakedFromProposal can update voterStakedProposals
        voterStakedProposalsCopy = voterStakedProposals[_from];

        while (
            i < voterStakedProposalsCopy.length &&
            withdrawnAmount < _targetAmount
        ) {
            uint256 proposalId = voterStakedProposalsCopy[i];
            Proposal storage proposal = proposals[proposalId];

            // In active proposals, we only subtract the needed amount to reach the target
            toWithdraw = MathHelpers.min256(
                _targetAmount.sub(withdrawnAmount),
                proposal.voterStake[_from]
            );
            if (toWithdraw > 0) {
                _withdrawStakedFromProposal(proposalId, toWithdraw, _from);
                withdrawnAmount = withdrawnAmount.add(toWithdraw);
            }
            i++;
        }
    }

    /**
     * @dev Withdraw an amount of tokens from a proposal
     * @param _proposalId Proposal id
     * @param _amount Amount of withdrawn tokens
     * @param _from Account to withdraw from
     */
    function _withdrawStakedFromProposal(
        uint256 _proposalId,
        uint256 _amount,
        address _from
    ) internal {
        Proposal storage proposal = proposals[_proposalId];
        require(proposal.proposalStatus != ProposalStatus.Executed, ERROR_CAN_NOT_WHITHDRAW_WHILE_PROJECT_EXECUTED);
        require(
            proposal.voterStake[_from] >= _amount,
            ERROR_WITHDRAW_MORE_THAN_STAKED
        );
        require(_amount > 0, ERROR_AMOUNT_CAN_NOT_BE_ZERO);

        uint256 previousStake = proposal.stakedTokens;
        proposal.stakedTokens = proposal.stakedTokens.sub(_amount);
        proposal.voterStake[_from] = proposal.voterStake[_from].sub(_amount);
        totalVoterStake[_from] = totalVoterStake[_from].sub(_amount);
        totalStaked = totalStaked.sub(_amount);

        if (proposal.voterStake[_from] == 0) {
            voterStakedProposals[_from].deleteItem(_proposalId);
        }

        if (proposal.proposalStatus == ProposalStatus.Active) {
            _calculateAndSetConviction(proposal, previousStake);
        }

        emit StakeWithdrawn(
            _from,
            _proposalId,
            _amount,
            proposal.voterStake[_from],
            proposal.stakedTokens,
            proposal.convictionLast
        );
    }
}

